package javacode;

public class Test27 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		
		
		for(int i = 10; i < 10; i++) { //손에 익을 수 있게 연습많이
			System.out.println("for OK");

		}
		//while 문
		
		int j = 10;
		while(j < 10) {
			System.out.println();
			j++;
		}
		// do~while문
		
		int k = 10;
		do {
			System.out.println("do-while OK");
			k++;
		} while(k < 10);
	}

}
